import React, { useState, useContext } from 'react';
import axios from 'axios';
// Import your authentication context if you're using one
// import { AuthContext } from '../context/AuthContext';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  
  // Uncomment and use if you're working with an AuthContext
  // const authContext = useContext(AuthContext);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5500/login', credentials);
      // If login is successful, update the authentication state
      onLogin(response.data);
      // If using context, you can set the state like this:
      // authContext.setAuthState(response.data);

      // Optionally, handle redirect to the home page or dashboard upon login
      // navigate('/dashboard'); // If you have a `useNavigate` hook
    } catch (error) {
      console.error('Login error', error.response || error);
      // Handle errors, e.g. show an alert or set an error message in state
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email">Email</label>
          <input
            type="email"
            name="email"
            value={credentials.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
