import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Checkout = () => {
  const [formData, setFormData] = useState({
    title: "",
    name: "",
    email: "",
    phone: "",
    address1: "",
    address2: "",
    city: "",
    state: "",
    zip: "",
    country: "",
    paymentMethod: "",
    captcha: "",
  });

  const navigate = useNavigate(); // Hook for navigation

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value }); // Update form data
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form behavior

    try {
      await axios.post("http://localhost:5500/checkout", formData); // Send form data to backend
      // Navigate to MyOrder on successful submission
      navigate("/myorder");
    } catch (error) {
      console.error("Error during checkout:", error); // Log any errors

      // Navigate to MyOrder even in case of error (customize as needed)
      navigate("/myorder");
    }
  };

  return (
    <>
      <div
        style={{
          background: "url('/images/hero-img.jpeg')",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          height: "30vh",
        }}
      />
      <section className="py-5">
        <div className="container">
          <h2 className="display-4" style={{ marginBottom: "30px" }}>
            Checkout
          </h2>
          <form onSubmit={handleSubmit}>
            <div className="row g-4">
              {/* Input fields */}
              <div className="col-sm-6 col-xs-12">
                <label>Title:</label>
                <select
                  className="form-control"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                >
                  <option value="">-- Choose --</option>
                  <option value="miss">Miss</option>
                  <option value="mr">Mr.</option>
                  <option value="mrs">Mrs.</option>
                  <option value="ms">Ms.</option>
                </select>
              </div>

              <div className="col-sm-6">
                <label>Name:</label>
                <input
                  name="name"
                  type="text"
                  className="form-control"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>

              {/* Additional form fields for other details */}
              
              <div className="col-lg-12">
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </div>
            </div>
          </form>
        </div>
      </section>
    </>
  );
};

export default Checkout;
