import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [items, setItems] = useState([]);
  const [activeCategory, setActiveCategory] = useState('Pizza');

  useEffect(() => {
    // Function to fetch items
    const fetchItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/items/${activeCategory}`);
        setItems(response.data);
      } catch (error) {
        console.error('Error fetching items', error);
      }
    };

    fetchItems();
  }, [activeCategory]);

  return (
    <div className="container">
      <h1>Eat@Home</h1>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="navbar-nav mr-auto">
          {['Pizza', 'Sandwich', 'Salad', 'Beverages', 'Extra'].map((category) => (
            <a
              key={category}
              className={`nav-item nav-link ${activeCategory === category ? 'active' : ''}`}
              href="#"
              onClick={(e) => {
                e.preventDefault();
                setActiveCategory(category);
              }}
            >
              {category}
            </a>
          ))}
        </div>
        <div className="navbar-nav">
          <a className="nav-item nav-link" href="/about-us">About Us</a>
          <a className="nav-item nav-link" href="/contact-us">Contact Us</a>
          <a className="nav-item nav-link" href="/login">Login</a>
          <a className="nav-item nav-link" href="/cart">
            Cart
            {/* If you have a cart count state, you can display it here */}
            {/* <span className="badge badge-pill badge-secondary">{cartCount}</span> */}
          </a>
        </div>
      </nav>
      <div className="row">
        {items.map((item) => (
          <div key={item._id} className="col-sm-6 col-md-4 col-lg-3 mb-3">
            <div className="card">
              <img src={`/images/${item.photo}`} className="card-img-top" alt={item.name} />
              <div className="card-body">
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">Â£{item.price}</p>
                <a href="#" className="btn btn-primary">Show Details</a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
