import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import './Login.css'; // Ensure you have a CSS file for custom styles

const Login = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5500/login', credentials);
      if (response.data.userId) {
        login(response.data.userId);
        navigate('/');
      }
    } catch (error) {
      console.error('Login failed:', error);
      alert('Invalid Credentials Details.');
    }
  };

  return (
    <section className="text-center text-lg-start">
      <div className="container py-4">
        <div className="row g-0 align-items-center">
          <div className="col-lg-6 mb-5 mb-lg-0">
            <div
              className="card bg-body-tertiary cascading-right"
              style={{ backdropFilter: 'blur(30px)' }}
            >
              <div className="card-body p-5 shadow-5 text-center">
                <h2 className="fw-bold mb-5">Login<span style={{ color: 'red' }}>.</span></h2>
                <form onSubmit={handleSubmit}>
                  <div className="form-outline mb-4">
                    <input
                      type="email"
                      name="email"
                      className="form-control"
                      value={credentials.email}
                      onChange={handleChange}
                      required
                    />
                    <label className="form-label">Email</label>
                  </div>
                  <div className="form-outline mb-4">
                    <input
                      type="password"
                      name="password"
                      className="form-control"
                      value={credentials.password}
                      onChange={handleChange}
                      required
                    />
                    <label className="form-label">Password</label>
                  </div>
                  <button type="submit" className="btn btn-primary btn-block mb-4">
                    Login
                  </button>
                  <br></br>
                  <Link to="/forgetpassword">Forgot Password?</Link>
                  <br />
                  <br />
                  <Link to="/signup">Don't have an account? Sign up</Link>
                </form>
              </div>
            </div>
          </div>
          <div className="col-lg-6 mb-5 mb-lg-0">
            <img
              src="/images/about-2.jpg"
              className="w-100 rounded-4 shadow-4"
              alt="Login Banner"
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
