import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ShowCart.css'; // Ensure this CSS file has custom styling for your cart

const ShowCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();
  
  // Retrieve userId from session storage
  const userId = sessionStorage.getItem('userId');
  
  useEffect(() => {
    if (!userId) {
      alert('Please log in to view your cart.');
      navigate('/login');
      return;
    }

    const fetchCartItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/cart/${userId}`);
        setCartItems(response.data);

        // Calculate and set total price
        const total = response.data.reduce((sum, item) => sum + item.qty * item.price, 0);
        setTotalPrice(total.toFixed(2));
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };

    fetchCartItems();
  }, [userId, navigate]);

  const removeItemFromCart = async (itemId) => {
    if (!userId) {
      alert('Please log in to remove items from your cart.');
      navigate('/login');
      return;
    }

    try {
      await axios.delete(`http://localhost:5500/cart/${userId}/${itemId}`);
      
      // Update the cartItems state to remove the deleted item
      const updatedCartItems = cartItems.filter((item) => item.itemId !== itemId);
      setCartItems(updatedCartItems);

      // Recalculate the total price
      const total = updatedCartItems.reduce((sum, item) => sum + item.qty * item.price, 0);
      setTotalPrice(total.toFixed(2));
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const handleConfirmOrder = async () => {
    if (!userId) {
      alert('Please log in to confirm your order.');
      navigate('/login', { state: { userId, cartItems } });
      return;
    }

    try {
      await axios.post('http://localhost:5500/order', { userId });
      navigate('/checkout');
    } catch (error) {
      console.error('Error confirming order:', error);
    }
  };

  return (
    <div className="card">
      <div className="row">
        <div className="col-md-8 cart">
          <div className="title">
            <div className="row">
              <div className="col">
                <h4><b>Shopping Cart</b></h4>
              </div>
              <div className="col text-right text-muted">
                {cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}
              </div>
            </div>
          </div>
          
          {cartItems.map((item) => (
            <div key={item.itemId} className="row border-top border-bottom">
              <div className="row main align-items-center">
                <div className="col-2">
                  <img className="img-fluid" src={`/images/${item.photo}`} alt={item.name} />
                </div>
                <div className="col">
                  <div className="row text-muted">{item.category}</div>
                  <div className="row">{item.name}</div>
                </div>
                <div className="col">
                  <button onClick={() => {}}>-</button>
                  <span className="border"> {item.qty} </span>
                  <button onClick={() => {}}>+</button>
                </div>
                <div className="col">
                  £{(item.qty * item.price).toFixed(2)}
                  <span
                    className="close"
                    onClick={() => removeItemFromCart(item.itemId)}
                  >
                    &#10005;
                  </span>
                </div>
              </div>
            </div>
          ))}

          <div className="back-to-shop">
            <a href="#">&larr;</a>
            <span className="text-muted">Back to shop</span>
          </div>
        </div>

        <div className="col-md-4 summary">
          <div><h5><b>Summary</b></h5></div>
          <hr />
          <div className="row">
            <div className="col">ITEMS {cartItems.length}</div>
            <div className="col text-right">£{totalPrice}</div>
          </div>
          
          <div className="row" >
            <div className="col">TOTAL PRICE</div>
            <div className="col text-right">£{totalPrice}</div>
          </div>
          <button className="btn btn-primary" onClick={handleConfirmOrder}>Confirm Order</button>
        </div>
      </div>
    </div>
  );
};

export default ShowCart;
