// components/ForgetPassword.js
import React, { useState } from "react";
import axios from "axios";

const ForgetPassword = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirm_password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/api/forgetpassword", formData);
      console.log(response.data);
      // Reset form after successful submission
      setFormData({ email: "", password: "", confirm_password: "" });
      alert("Password reset successful!");
    } catch (error) {
      console.error("Error resetting password:", error);
      alert("Error resetting password. Please try again later.");
    }
  };

  return (
    <>
      <div
        style={{
          background:
            "url('')",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          height: "30vh",
        }}
      ></div>
      <section className="py-5">
        <div className="container text-center">
          <h2 className="display-4" style={{ marginBottom: "30px" }}>
            Forget Password
          </h2>
          <form className="row g-4" onSubmit={handleSubmit}>
            <div className="col-12">
              <input
                className="form-control"
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-6">
              <input
                className="form-control"
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-6">
              <input
                className="form-control"
                type="password"
                name="confirm_password"
                placeholder="Confirm Password"
                value={formData.confirm_password}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-12">
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
            </div>
          </form>
        </div>
      </section>
    </>
  );
};

export default ForgetPassword;
