import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ItemDetails = () => {
  const [item, setItem] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const { id } = useParams();

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/item/${id}`);
        setItem(response.data);
      } catch (error) {
        console.error('Error fetching item details', error);
      }
    };

    fetchItemDetails();
  }, [id]);

  const handleQuantityChange = (increment) => {
    setQuantity((prevQuantity) => {
      if (increment) {
        return prevQuantity + 1;
      }
      return prevQuantity > 1 ? prevQuantity - 1 : 1;
    });
  };

  if (!item) return <div>Loading...</div>;

  return (
    <div className="item-details-container">
      <img src={`/images/${item.photo}`} alt={item.name} />
      <h2>{item.name}</h2>
      <p>Price: Â£{item.price}</p>
      <div className="quantity-controls">
        <button onClick={() => handleQuantityChange(false)}>-</button>
        <input type="text" value={quantity} readOnly />
        <button onClick={() => handleQuantityChange(true)}>+</button>
      </div>
      <button>Add To Cart</button>
    </div>
  );
};

export default ItemDetails;
