import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import Home from './Home'; 
import AboutUs from './AboutUs';
import ContactUs from './ContactUs'; 
import ItemDetails from './ItemDetails';
import Login from './Login';

const App = () => {

  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = (credentials) => {
    // Here you would normally make an API call with the credentials
    // For this example, let's assume the login is successful
    setIsAuthenticated(true);
    // You would also handle login persistence here (e.g., using local storage)
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    // Also clear any stored login state in local storage or cookie
  };

  return (
    <Router>
      <div className="container">
        <header className="d-flex justify-content-between align-items-center py-3 border-bottom">
          <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
            <h1>Eat@Home</h1>
          </Link>
          <div>
            <Link to="/contact-us" style={{ textDecoration: 'none' }} className="px-2">Contact Us</Link>
            <Link to="/about-us" style={{ textDecoration: 'none' }} className="px-2">About Us</Link>
             { !isAuthenticated && <Link to="/login" className="px-2">Login</Link> }
            { isAuthenticated && <button onClick={handleLogout}>Logout</button> }
            <Link to="/cart" className="px-2">
              <img src='/images/cart.png' alt='Cart' style={{ height: '32px' }} />
            </Link>
          </div>
        </header>
      </div>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about-us" element={<AboutUs />} />
        <Route path="/contact-us" element={<ContactUs />} />
        <Route path="/items/:id" element={<ItemDetails />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </Router>
  );
};

export default App;
