import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 
import './Home.css'; // Ensure you have a corresponding CSS file for custom styles

const Home = () => {
  const [items, setItems] = useState([]);
  const [activeCategory, setActiveCategory] = useState('Cakes');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/items/${activeCategory}`);
        setItems(response.data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, [activeCategory]);

  const categories = ['Cakes', 'Waffles', 'Pancakes', 'Tarts'];

  return (
    <section id="menu" className="menu">
      <div className="container" data-aos="fade-up">

        <div className="section-header">
          <p>Check Our <span>Dessert-a-licious Menu</span><span style={{ color: 'red' }}>.</span></p>
        </div>

        <ul className="nav nav-tabs d-flex justify-content-center" data-aos="fade-up" data-aos-delay="200">
          {categories.map((category) => (
            <li className="nav-item" key={category}>
              <a
                className={`nav-link ${activeCategory === category ? 'active' : ''}`}
                onClick={() => setActiveCategory(category)}
                style={{ color: 'black' }} // Set color to black
              >
                <h4>{category}</h4>
              </a>
            </li>
          ))}
        </ul>

        <div className="tab-content" data-aos="fade-up" data-aos-delay="300">
          {categories.map((category) => (
            <div
              key={category}
              className={`tab-pane fade ${activeCategory === category ? 'active show' : ''}`}
            >
              <div className="row gy-5">
                {items.map((item) => (
                  <div key={item._id} className="col-lg-4 menu-item">
                    <img src={`/images/${item.photo}`} className="menu-img img-fluid" alt={item.name} />
                    <h4>{item.name}</h4>
                    <p className="price">Â£{item.price}</p>
                    <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                      <div className="text-center">
                        <button
                          className="btn btn-outline-dark"
                          onClick={() => navigate(`/items/${item._id}`)}
                        >
                          Show Details
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

      </div>
    </section>
  );
};



export default Home;
