import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ShowCart.css';

// Reusable CartItem component
const CartItem = ({ item, removeFromCart, updateCartItemQuantity }) => (
  <div className="cart-item">
    <img src={`/images/${item.photo}`} alt={item.name} />
    <div>
      <h4>{item.name}</h4>
      <p>Â£{item.price} x {item.qty}</p>
      <div className="action-buttons">
        <button
          className="btn btn-sm btn-danger"
          onClick={() => removeFromCart(item.itemId)}
        >
          Remove
        </button>
        <input
          type="number"
          min="1"
          value={item.qty}
          onChange={(e) => updateCartItemQuantity(item.itemId, parseInt(e.target.value))}
        />
      </div>
    </div>
  </div>
);

const ShowCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  const userId = sessionStorage.getItem('userId');

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/cart/${userId}`);
        setCartItems(response.data);

        const total = response.data.reduce((sum, item) => sum + item.qty * item.price, 0);
        setTotalPrice(total.toFixed(2));
      } catch (error) {
        console.error('Error fetching cart items:', error);
        setError('Unable to fetch cart items.');
      } finally {
        setIsLoading(false);
      }
    };

    if (!userId) {
      alert('Please log in to view your cart.');
      navigate('/login');
    } else {
      fetchCartItems();
    }
  }, [userId, navigate]);

  const CartItem = ({ item, removeFromCart }) => {
    return (
      <div className="cart-item">
        <span>{item.name}</span>
        <button onClick={() => removeFromCart(item._id)}>Remove</button>
      </div>
    );
  };

  
  
  const updateCartItemQuantity = async (itemId, newQuantity) => {
    console.log("Attempting to update quantity for itemId:", itemId, "to:", newQuantity);
  
    if (newQuantity < 1) {
      alert("Quantity must be at least 1.");
      return;
    }
  
    try {
      await axios.put(`http://localhost:5500/cart/${userId}/${itemId}`, { quantity: newQuantity });
  
      const updatedCartItems = cartItems.map((item) => 
        item.itemId === itemId ? { ...item, qty: newQuantity } : item
      );
  
      const total = updatedCartItems.reduce((sum, item) => sum + item.qty * item.price, 0);
      setTotalPrice(total.toFixed(2));
      setCartItems(updatedCartItems);
    } catch (error) {
      console.error("Error updating item quantity:", error);
      setError("Unable to update item quantity.");
    }
  };
  

  const handleConfirmOrder = async () => {
    if (!userId) {
      alert('Please log in to confirm your order.');
      navigate('/login');
      return;
    }

    try {
      await axios.post('http://localhost:5500/order', { userId });
      navigate('/my-order');
    } catch (error) {
      console.error('Error confirming order:', error);
      setError('Unable to confirm order.');
    }
  };

  if (isLoading) {
    return <p>Loading your cart...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="row">
      <div className="col-sm-4"></div>
      <div className="col-sm-4">
        <h2>Cart</h2>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            {cartItems.map((item) => (
              <CartItem 
                key={item.itemId}
                item={item}
                removeFromCart={removeFromCart}
                updateCartItemQuantity={updateCartItemQuantity}
              />
            ))}
            <div className="total-price">
              <h3>Total: Â£{totalPrice}</h3>
            </div>
            <button className="btn btn-primary" onClick={handleConfirmOrder}>
              Confirm Order
            </button>
          </>
        )}
      </div>
      <div className="col-sm-4"></div>
    </div>
  );
};

export default ShowCart;
