import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import AboutUs from './AboutUs'; // Assuming you have an AboutUs.js file in your src directory

const App = () => {
  const [items, setItems] = useState([]);
  const [activeCategory, setActiveCategory] = useState('Pizza');

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/items/${activeCategory}`);
        setItems(response.data);
      } catch (error) {
        console.error('Error fetching items', error);
      }
    };

    fetchItems();
  }, [activeCategory]);

  return (
    <Router>
      <div className="container">
        <header className="d-flex justify-content-between align-items-center py-3 border-bottom">
          <h1>Eat@Home</h1>
          <div>
            <Link to="/contact-us" style={{ textDecoration: 'none' }} className="px-2">Contact Us</Link>
            <Link to="/about-us" style={{ textDecoration: 'none' }} className="px-2">About Us</Link>
            <Link to="/login" style={{ textDecoration: 'none' }} className="px-2">Login</Link>
            <Link to="/cart" className="px-2">
              <img src='/images/cart.png' alt='Cart' style={{ height: '32px' }} />
            </Link>
          </div>
        </header>
        <nav className="nav nav-pills flex-column flex-sm-row my-3">
          {['Pizza', 'Sandwich', 'Salad', 'Beverages', 'Extra'].map((category) => (
            <Link
              key={category}
              to={`/${category.toLowerCase()}`}
              className={`flex-sm-fill text-sm-center nav-link ${activeCategory === category ? 'active' : ''}`}
              onClick={() => setActiveCategory(category)}
            >
              {category}
            </Link>
          ))}
        </nav>
        <div className="row">
          {items.map((item) => (
            <div key={item._id} className="col-sm-6 col-md-4 col-lg-3 mb-3">
              <div className="card">
                <img src={`/images/${item.photo}`} className="card-img-top" alt={item.name} />
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p className="card-text">£{item.price}</p>
                  <button className="btn btn-primary">Show Details</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Routes>
        <Route path="/about-us" element={<AboutUs />} />
        {/* Define other routes here */}
      </Routes>
    </Router>
  );
};

export default App;
