import React from 'react';
import './HeroSection.css'; // Import the CSS for styling

export default function HeroImage() {
  return (
    <header className="hero-section">
      <div
        className="bg-image"
        style={{ backgroundImage: "url('/images/hero-img.jpg')", height: 400 }}
      >
        <div className="mask" style={{ backgroundColor: 'rgba(0, 0, 0, 0.6)' }}>
          <div className="hero-content"> {/* Adjusted layout for left alignment */}
            <div className="hero-text">
              <h1>Crafted with Love, Served with Joy</h1>
              <h4>Welcome to Our Dessert Paradise!</h4>
              <a className="btn btn-outline-light btn-lg" href="#!" role="button">
                Order Now
              </a>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}
