import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [items, setItems] = useState([]);
  const [activeCategory, setActiveCategory] = useState('Pizza');

  useEffect(() => {
    // Function to fetch items
    const fetchItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/items/${activeCategory}`);
        setItems(response.data);
      } catch (error) {
        console.error('Error fetching items', error);
      }
    };

    fetchItems();
  }, [activeCategory]);

  return (
    <div className="container">
      <header className="d-flex justify-content-between align-items-center py-3 border-bottom">
        <h1>Eat@Home</h1>
        <div>
          <a href="/contact-us" style={{ textDecoration: 'none' }} className="px-2">Contact Us</a>
          <a href="/about-us" style={{ textDecoration: 'none' }} className="px-2">About Us</a>
          <a href="/login" style={{ textDecoration: 'none' }} className="px-2">Login</a>
          <a href="/cart" className="px-2">
            <img src='images/cart.png' alt='Cart' height={64} width={64} /> 
          </a>
        </div>
      </header>
      <nav className="nav nav-pills flex-column flex-sm-row my-3">
        {['Pizza', 'Sandwich', 'Salad', 'Beverages', 'Extra'].map((category) => (
          <a
            key={category}
            className={`flex-sm-fill text-sm-center nav-link ${activeCategory === category ? 'active' : ''}`}
            href="#"
            onClick={(e) => {
              e.preventDefault();
              setActiveCategory(category);
            }}
          >
            {category}
          </a>
        ))}
      </nav>
      <div className="row">
        {items.map((item) => (
          <div key={item._id} className="col-sm-6 col-md-4 col-lg-3 mb-3">
            <div className="card">
              <img src={`/images/${item.photo}`} className="card-img-top" alt={item.name} />
              <div className="card-body">
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">Â£{item.price}</p>
                <a href="#" className="btn btn-primary">Show Details</a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
