import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductManager.css';

const ProductManager = () => {
  const [products, setProducts] = useState([]);
  const [totalValue, setTotalValue] = useState(0);

  // Fetch products from the backend when the component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5500/products');
        setProducts(response.data);
        const total = response.data.reduce((sum, product) => sum + product.price * product.stock, 0);
        setTotalValue(total.toFixed(2));
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addProduct = async (name, price, stock) => {
    try {
      const response = await axios.post('http://localhost:5500/products', { name, price, stock });
      const newProduct = response.data;
      setProducts([...products, newProduct]);
      setTotalValue((totalValue + newProduct.price * newProduct.stock).toFixed(2));
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const removeProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:5500/products/${productId}`);
      const updatedProducts = products.filter(product => product.id !== productId);
      setProducts(updatedProducts);
      const total = updatedProducts.reduce((sum, product) => sum + product.price * product.stock, 0);
      setTotalValue(total.toFixed(2));
    } catch (error) {
      console.error('Error removing product:', error);
    }
  };

  const updateProduct = async (productId, newStock) => {
    try {
      await axios.put(`http://localhost:5500/products/${productId}`, { stock: newStock });
      const updatedProducts = products.map(product =>
        product.id === productId ? { ...product, stock: newStock } : product
      );
      setProducts(updatedProducts);
      const total = updatedProducts.reduce((sum, product) => sum + product.price * product.stock, 0);
      setTotalValue(total.toFixed(2));
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  const handleAddProduct = () => {
    const productName = prompt('Enter product name:');
    const productPrice = parseFloat(prompt('Enter product price:'));
    const productStock = parseInt(prompt('Enter product stock:'), 10);

    if (productName && !isNaN(productPrice) && !isNaN(productStock)) {
      addProduct(productName, productPrice, productStock);
    } else {
      alert('Invalid input. Please enter correct product information.');
    }
  };

  return (
    <div className='product-manager'>
      <h2>Product Manager</h2>
      <button className='btn btn-primary' onClick={handleAddProduct}>Add Product</button>
      <div className='product-list'>
        {products.map(product => (
          <div key={product.id} className="product-item">
            <h4>{product.name}</h4>
            <p>Price: £{product.price}</p>
            <p>Stock: {product.stock}</p>
            <button className='btn btn-sm btn-danger' onClick={() => removeProduct(product.id)}>Remove</button>
            <input
              type='number'
              value={product.stock}
              onChange={(e) => updateProduct(product.id, parseInt(e.target.value))}
              min="0"
            />
          </div>
        ))}
      </div>
      <div className="total-value">
        <h3>Total Inventory Value: £{totalValue}</h3>
      </div>
    </div>
  );
};

export default ProductManager;
