import React from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const navigate =      navigate('/PaymentPage');
  ;

  const handleFinish = async (e) => {
    e.preventDefault(); // Prevent default form behavior

    const paymentData = {
      title: document.querySelector("[name='title']").value,
      name: document.querySelector("[name='name']").value,
      email: document.querySelector("[name='email']").value,
      phone: document.querySelector("[name='phone']").value,
      address1: document.querySelector("[name='address1']").value,
      address2: document.querySelector("[name='address2']").value,
      city: document.querySelector("[name='city']").value,
      state: document.querySelector("[name='state']").value,
      zip: document.querySelector("[name='zip']").value,
      country: document.querySelector("[name='country']").value,
      paymentMethod: document.querySelector("[name='paymentMethod']").value,
    };

    try {
      const response = await axios.post("http://localhost:5500/payment", paymentData); // Correct endpoint

      if (response.status === 201) {
        navigate("/my-order"); // Redirect on success
      } else {
        console.error("Payment error:", response.data.message);
      }
    } catch (error) {
      console.error("Error processing payment:", error);
    }
  };

  return (
    <form onSubmit={handleFinish}>
      {/* Form fields */}
      <div>
        <button type="submit" className="btn btn-primary">Finish</button> {/* Submit form */}
      </div>
    </form>
  );
};

export default PaymentPage;
