import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ShowCart.css';

const ShowCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  const userId = sessionStorage.getItem('userId');

  useEffect(() => {
    if (!userId) {
      alert('Please log in to view your cart.');
      navigate('/login');
      return;
    }

    const fetchCartItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/cart/${userId}`);
        setCartItems(response.data);

        const total = response.data.reduce((sum, item) => sum + item.qty * item.price, 0);
        setTotalPrice(total.toFixed(2));
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };

    fetchCartItems();
  }, [userId, navigate]);

  const removeFromCart = async (itemId) => {
    try {
      await axios.delete(`http://localhost:5500/cart/${userId}/${itemId}`);
      const updatedCartItems = cartItems.filter(item => item.itemId !== itemId);
      setCartItems(updatedCartItems);
      const total = updatedCartItems.reduce((sum, item) => sum + item.qty * item.price, 0);
      setTotalPrice(total.toFixed(2));
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const updateCartItemQuantity = async (itemId, newQuantity) => {
    try {
      await axios.put(`http://localhost:5500/cart/${userId}/${itemId}`, { quantity: newQuantity });
      const updatedCartItems = cartItems.map(item =>
        item.itemId === itemId ? { ...item, qty: newQuantity } : item
      );
      setCartItems(updatedCartItems);
      const total = updatedCartItems.reduce((sum, item) => sum + item.qty * item.price, 0);
      setTotalPrice(total.toFixed(2));
    } catch (error) {
      console.error('Error updating item quantity in cart:', error);
    }
  };

  const handleConfirmOrder = async () => {
    if (!userId) {
      alert('Please log in to confirm your order.');
      navigate('/login');
      return;
    }

    try {
      await axios.post('http://localhost:5500/order', { userId });
      navigate('/my-order');
    } catch (error) {
      console.error('Error confirming order:', error);
    }
  };

  return (
    <div className='row'>
      <div className='col-sm-4'></div>
      <div className='col-sm-4'>
        <h2>Cart</h2>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            {cartItems.map((item) => (
              <div key={item.itemId} className="cart-item">
                <img src={`/images/${item.photo}`} alt={item.name} />
                <div>
                  <h4>{item.name}</h4>
                  <p>£{item.price} x {item.qty}</p>
                  <div className="action-buttons">
                    <button className="btn btn-sm btn-danger" onClick={() => removeFromCart(item.itemId)}>Remove</button>
                    <input
                      type='number'
                      value={item.qty}
                      onChange={(e) => updateCartItemQuantity(item.itemId, parseInt(e.target.value))}
                      min="1"
                    />
                  </div>
                </div>
              </div>
            ))}
            <div className="total-price">
              <h3>Total £{totalPrice}</h3>
            </div>
            <button className='btn btn-primary' onClick={handleConfirmOrder}>Confirm Order</button>
          </>
        )}
      </div>
      <div className='col-sm-4'></div>
    </div>
  );
};

export default ShowCart;
