import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const [name, setName] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCVV] = useState('');
  const [address, setAddress] = useState('');
  const navigate = useNavigate();

  const handlePayment = async () => {
    try {
      const paymentDetails = {
        name,
        cardNumber,
        expiryDate,
        cvv,
        address,
      };

      // Send the payment details to the backend
      await axios.post('http://localhost:5500/payment', paymentDetails);

      // Navigate to the order confirmation page or thank you page
      navigate('/order-confirmation');
    } catch (error) {
      console.error('Error processing payment:', error);
    }
  };

  return (
    <div className="payment-page">
      <h2>Enter Payment Details</h2>
      <form onSubmit={(e) => { e.preventDefault(); handlePayment(); }}>
        <div>
          <label>Name on Card</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Card Number</label>
          <input
            type="text"
            pattern="\d{16}" // Only allows 16-digit card numbers
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Expiry Date</label>
          <input
            type="text"
            placeholder="MM/YY"
            value={expiryDate}
            onChange={(e) => setExpiryDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>CVV</label>
          <input
            type="text"
            pattern="\d{3}" // 3-digit CVV
            value={cvv}
            onChange={(e) => setCVV(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Billing Address</label>
          <textarea
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>
        <button type="submit">Submit Payment</button>
      </form>
    </div>
  );
};

export default PaymentPage;
