import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';
import AboutUs from './components/AboutUs'; // Make sure to import your AboutUs component

const App = () => {
  const [items, setItems] = useState([]);
  const [activeCategory, setActiveCategory] = useState('Pizza');

  useEffect(() => {
    // Function to fetch items
    const fetchItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5500/items/${activeCategory}`);
        setItems(response.data);
      } catch (error) {
        console.error('Error fetching items', error);
      }
    };

    fetchItems();
  }, [activeCategory]);

  return (
    <Router>
      <div className="container">
        <header className="d-flex justify-content-between align-items-center py-3 border-bottom">
          <h1>Eat@Home</h1>
          <div>
            <Link to="/contact-us" className="px-2" style={{ textDecoration: 'none' }}>Contact Us</Link>
            <Link to="/about-us" className="px-2" style={{ textDecoration: 'none' }}>About Us</Link>
            <Link to="/login" className="px-2" style={{ textDecoration: 'none' }}>Login</Link>
            <Link to="/cart" className="px-2">
              <img src='/images/cart.png' alt='Cart' style={{ height: '32px' }} />
            </Link>
          </div>
        </header>
        {/* Navigation bar for categories */}
        {/* ... rest of your code for navigation and items */}
      </div>

      <Switch>
        <Route path="/about-us" component={AboutUs} />
        {/* Add routes for other links as needed */}
      </Switch>
    </Router>
  );
};

export default App;
